public class Example {
  private List<ClassName> enclosingClasses() {
    // Initialize an empty list to hold the result.
    List<ClassName> result = new ArrayList<>();

    // Iterate over each nested class in this class, starting with the outermost one.
    for (ClassName c = this; c != null; c = c.enclosingClassName) {
      // Add the current class to the list of enclosing classes.
      result.add(c);
    }

    // Return the list of enclosing classes, in reverse order.
    return result;
  }

  public static void main(String[] args) {
    ClassName className = new ClassName("example", null, "Example");

    // Use the enclosingClasses method to retrieve the nested class hierarchy.
    for (ClassName c : className.enclosingClasses()) {
      System.out.println(c);
    }
  }
}

