public void example() {
  Builder builder = new Builder();

  // Prints the opening bracket for the first control flow
  // followed by the given string
  builder.nextControlFlow("if (x == 5)", "x");

  // Prints an indented block of code inside the first control flow
  builder.indent();
  builder.addStatement(String.format("System.out.println(\"%s\");", "hello"));
  builder.unindent();

  // Prints a closing bracket for the first control flow
  // followed by an opening bracket for the second control flow
  builder.nextControlFlow("else if (x == 10)", "x");

  // Prints another indented block of code inside the second control flow
  builder.indent();
  builder.addStatement(String.format("System.out.println(\"%s\");", "world"));
  builder.unindent();

  // Prints a closing bracket for both control flows
  builder.endControlFlow();
}

public class MyCodeBuilder {
    private static final String NEWLINE = System.lineSeparator();
    private static final String INDENTATION = "\t";

    public Builder(String name) {
        this.name = name;
        this.controlFlowDepth = 0;
        this.controlFlowIndentation = "";
    }

    public Builder beginControlFlow(String controlFlow, Object... args) {
        controlFlowDepth++;
        add("$[");
        add(controlFlow + " {\n", args);
        controlFlowIndentation += INDENTATION;
        return this;
    }

    public Builder nextControlFlow(String controlFlow, Object... args) {
        if (controlFlowDepth > 0) {
            unindent();
            add("} " + controlFlow + " {\n", args);
            indent();
        } else {
            add("$<");
            add(controlFlow + " {\n", args);
        }
        return this;
    }

    public Builder endControlFlow() {
        if (controlFlowDepth > 0) {
            unindent();
            add("}\n");
            controlFlowDepth--;
        } else {
            add("$<");
            add(controlFlow + ";\n", args);
        }
        return this;
    }

    public Builder indent() {
        controlFlowIndentation += INDENTATION;
        return this;
    }

    public Builder unindent() {
        controlFlowIndentation = controlFlowIndentation.substring(0, controlFlowIndentation.length - 1);
        return this;
    }
}

public class ControlFlowExample {
  public static void main(String[] args) {
    int i = 5;
    CodeBlock.Builder builder = new CodeBlock.Builder();
    if (i == 10) {
      builder.beginControlFlow("if (foo == 10)", i);
    } else {
      builder.nextControlFlow("else", i);
    }

    // Code in between the control flow statements will be indented, but not included in the generated code.
    builder.addStatement("System.out.println(i)");

    // End the control flow construct and generate the final code block.
    builder.endControlFlow();

    // Print the generated code to standard output.
    System.out.println(builder.build());
  }
}

