void close() throws IOException {
    if (nextFlush != null) flush(nextFlush);
    closed = true;
}

private void writeLine(String text, FlushType flushType) throws IOException {
    // Write the line to the output stream with a newline character if the line will exceed it's limit, otherwise do nothing.
    if (column + text.length() > limit) {
        wrap();
        nextFlush = FlushType.WRAP;
        indentLevel++;
    } else {
        space();
        nextFlush = FlushType.SPACE;
        indentLevel++;
    }

    buffer.append(text);
    flush(flushType);
}

private void wrap() throws IOException {
    // Write a newline character if the line will exceed it's limit, otherwise do nothing.
    if (column > limit) {
        out.append('\n');
        column = 0;
    }
}

private void space() throws IOException {
    // Write a space character if the line will not exceed its limit, otherwise do nothing.
    if (column == 0) {
        return;
    } else {
        out.append(' ');
    }
}

