@Test public void errorTypes() {
    JavaFileObject hasErrorTypes =
        JavaFileObjects.forSourceLines(
            "com.squareup.tacos.ErrorTypes",
            "package com.squareup.tacos;",
            "",
            "@SuppressWarnings(\"hook-into-compiler\")",
            "class ErrorTypes {",
            "  Tacos tacos;",
            "  Ingredients.Guacamole guacamole;",
            "}");
    Compilation compilation = javac().withProcessors(new AbstractProcessor() {
      @Override
      public boolean process(Set<? extends TypeElement> set, RoundEnvironment roundEnvironment) {
        TypeElement classFile =
            processingEnv.getElementUtils().getTypeElement("com.squareup.tacos.ErrorTypes");
        List<VariableElement> fields = fieldsIn(classFile.getEnclosedElements());
        ErrorType topLevel = (ErrorType) fields.get(0).asType();
        ErrorType member = (ErrorType) fields.get(1).asType();

        assertThat(TypeName.get(topLevel)).isEqualTo(ClassName.get("", "Tacos"));
        assertThat(TypeName.get(member)).isEqualTo(ClassName.get("Ingredients", "Guacamole"));
        return false;
      }

      @Override
      public Set<String> getSupportedAnnotationTypes() {
        return Collections.singleton("*");
      }
    }).compile(hasErrorTypes);

    assertThat(compilation).failed();
  }

@Override
public boolean process(Set<? extends TypeElement> set, RoundEnvironment roundEnvironment) {
    // Use the provided elements to generate some code
    for (TypeElement element : set) {
        // Do something with each type in set
        System.out.println("Processing " + element);
        // Get all fields declared within the type element
        List<VariableElement> fields = fieldsIn(element.getEnclosedElements());
        ErrorType topLevel = (ErrorType) fields.get(0).asType();
        ErrorType member = (ErrorType) fields.get(1).asType();

        // Get the fully qualified name of each type element in set
        String topName = topLevel.toString();
        String memName = member.toString();

        // Create a classname object for each type, and then call getSimpleName() to retrieve their simple names
        ClassName topClassName = ClassName.get(topName);
        ClassName memberClassName = ClassName.get(memName);

        // Print the simple names of both classes
        System.out.println("Simple name of top-level class: " + topClassName.getSimpleName());
        System.out.println("Simple name of member class: " + memberClassName.getSimpleName());
    }
    return false;
}

