@Test
public void importStaticForCrazyFormatsWorks() {
    JavaFile.builder("com.squareup.tacos", TypeSpec.classBuilder("Taco")
            .addStaticBlock(CodeBlock.builder()
                .addStatement("$T", Runtime.class) // Import the class name as a variable
                .addStatement("$T.a()", Runtime.class) // Access the a method on that imported class
                .addStatement("$T.X", Runtime.class) // Access the X field on that imported class
                .addStatement("$T$T", Runtime.class, Runtime.class) // Import the class twice as a variable
                .addStatement("$T.$T", Runtime.class, Runtime.class) // Access the class variable followed by another class variable
                .addStatement("$1T$1T", Runtime.class) // Import the class name once more as a variable
                .addStatement("$1T$2L$1T", Runtime.class, "?") // Import the class followed by an unknown literal followed by the class again
                .addStatement("$1T$2L$2S$1T", Runtime.class, "?") // Import the class followed by an unknown string followed by the class again
                .addStatement("$1T$2L$2S$1T$3N$1T", Runtime.class, "?", new Taco()) // Import the class followed by a method reference followed by the class again
                .addStatement("$T$L", Runtime.class, "?") // Import the class and unknown literal
                .addStatement("$T$S", Runtime.class, "?") // Import the class and unknown string
                .addStatement("$T$N", Runtime.class, new Taco()) // Import the class followed by a method reference
            )
            .build()
    ).addStaticImport(Runtime.class, "*") // Import all methods of the class Runtime.
    .build(); // Compile!
}

