package com.squareup.tacos;

import java.io.File;
import java.lang.Override;
import java.lang.SuppressWarnings;
import java.util.Arrays;

import static java.util.Arrays.asList;

public final class Taco {
  private final String name;
  private final File source;
  private final int[] numOfBeef;

  public Taco(String name, File source) {
    this.name = name;
    this.source = source;
    numOfBeef = new int[0];
  }

  public String getName() {
    return name;
  }

  public File getSource() {
    return source;
  }

  @Override
  public boolean equals(Object o) {
    if (this == o) return true;
    if (!(o instanceof Taco)) return false;
    Taco taco = (Taco) o;
    return name.equals(taco.name) && source.equals(taco.source);
  }

  @Override
  public int hashCode() {
    return java.util.Objects.hash(name, source);
  }
}

