public static class Test {
    @Rule
    public final CompilationRule compilation = new CompilationRule();

    @Override
    protected Elements getElements() {
        return compilation.getElements();
    }

    @Override
    protected Types getTypes() {
        return compilation.getTypes();
    }
}

@Override
public void evaluate() throws Throwable {
    final AtomicReference<Throwable> thrown = new AtomicReference<>();
    boolean successful = compile(ImmutableList.of(new AbstractProcessor() {
        @Override
        public SourceVersion getSupportedSourceVersion() {
            return SourceVersion.latest();
        }

        @Override
        public Set<String> getSupportedAnnotationTypes() {
            return ImmutableSet.of("*");
        }

        @Override
        public synchronized void init(ProcessingEnvironment processingEnv) {
            super.init(processingEnv);
            elements = processingEnv.getElementUtils();
            types = processingEnv.getTypeUtils();
        }

        @Override
        public boolean process(Set<? extends TypeElement> annotations, RoundEnvironment roundEnv) {
            // just run the test on the last round after compilation is over
            if (roundEnv.processingOver()) {
                try {
                    base.evaluate();
                } catch (Throwable e) {
                    thrown.set(e);
                }
            }
            return false;
        }
    }));
    checkState(successful);
    Throwable t = thrown.get();
    if (t != null) {
        throw t;
    }
}

@Override
public SourceVersion getSupportedSourceVersion() {
    return SourceVersion.latest();
}

@Override
public Set<String> getSupportedAnnotationTypes() {
    return ImmutableSet.of("*");
}

@Override
public synchronized void init(ProcessingEnvironment processingEnv) {
    super.init(processingEnv);
    elements = processingEnv.getElementUtils();
    types = processingEnv.getTypeUtils();
}

public class Test {
  @Test
  public void test() {
    // Just run the test on the last round after compilation is over
    if (roundEnv.processingOver()) {
      try {
        base.evaluate();
      } catch (Throwable e) {
        thrown.set(e);
      }
    }
  }
}

